package application;

public class Stringequals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String text1 = "apple";
		String text2 = "orange";
		
		System.out.printf("두개의 문자열 일치 : %b\n", text1 == text2);
		System.out.println("두개의 문자열 일치 : " + text1.equals(text2));
	}

}
